mapper.select:
select
    id, name, no, clazz_id, gender, phone, edu_id, last_update_time, create_time
from
    student


        and name like concat('%', #{name}, '%')


        and no like concat('%', #{no}, '%')


        and clazz_id = #{clazzId}


        and gender = #{gender}


        and phone like concat('%', #{phone}, '%')


        and edu_id = #{eduId}
